mapper.select:
select id id, user_name userName, pass_wd passWord
from user_login
where id = #{id}

select id id, user_name userName, pass_wd passWord,`role` role
from user_login
where user_name = #{userName} AND pass_wd=#{passWord}

select id id, user_name userName, pass_wd passWord,`role` role
from user_login
where user_name = #{userName}

mapper.update:
update user_login
set pass_wd = #{passWord}
where user_name = #{userName}

